      private string FakeRetrievePatternRX(HPRequest mReq) {
         string result;
         if (mReq.CharSize == 8 && mReq.Page == 1) {
            result = "";
         } else {
            result = "0203";
         }
         return AsciiToRawData(result);
      }

      private string FakeRetrievePatternPXR(HPRequest mReq) {
         string result = "0203";
         if (mReq.CharSize == 5 && mReq.Page == 1) {
            result = "";
         }
         return AsciiToRawData(result);
      }

